import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image'


function App() {

  const [movies, setMovies] = useState(null);

  useEffect(() => {
    fetch('/api/movies')
      .then((response) => response.json())
      .then(setMovies)
  }, []);

  if( movies == null) return null;

  return (
    <div className="App">
      <Container className="bg-light">
        <h1>Movie Reviews</h1>
        { movies.map( movie => { return <Movie info={movie}></Movie>}) }
      </Container>
    </div>
  );
}

function Movie(props) {
  console.log(props);
  return (
    <>
      <h2 className="mt-5">{props.info.name}</h2>
      <Container>
        <Row className="justify-content-md-center">
          <Col xs="auto">
            <Image rounded="true" src={`images/${props.info.poster}`}></Image>
          </Col>
          <Col xs="auto">
            <Row className="justify-content-md-left">
              <Col>
                <p className="fs-4 text-start">Date</p>
              </Col>
              <Col>
                <p className="fs-4 text-start">{props.info.date}</p>
              </Col>
            </Row>
            <Row className="justify-content-md-left">
              <Col>
                <p className="fs-4 text-start">Actors</p>
              </Col>
              <Col>
                {props.info.actors.map( actor => { return <Row><p className="fs-4 text-start">{actor}</p></Row>})}
              </Col>
            </Row>
            <Row className="justify-content-md-left">
              <Col>
                <p className="fs-4 text-start">Rating</p>
              </Col>
              <Col>
                <p className="fs-4 text-start">{props.info.rating}</p>
              </Col>
            </Row>

          </Col>
        </Row>
      </Container>
    </>
  )
}

export default App;
