import React from "react";
import ReactDOM from 'react-dom';
import { useState, useEffect } from 'react';
import './App.css';
import {Link, useLocation} from "react-router-dom"
import Emoji from 'a11y-react-emoji'
import Picker from 'emoji-picker-react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'


export function Home({movies, setMovies}) {
    console.log(movies)


    function handleRemove(name) {
        console.log(name)
        const remove = async () => {
            const result = await  fetch("/api/removeMovie",{
                method: "POST",
                body: JSON.stringify({name: name}),
                headers:{"Content-Type": "application/json",}
            });
            const body = await result.json();
            console.log(body);
            setMovies(body.movies)
        }
        remove();
        setMovies()
      }

    function emjoi(rating){
        if(rating == "1"){
            return <Emoji symbol="⭐" label="star"/>
        }else if(rating == "2"){
            return <><Emoji symbol="⭐" label="star" /><Emoji symbol="⭐" label="star" /></>
        }
    }
    return (
        <div className="App">
            <Navbar bg="light" expand="lg">
                <Container>
                    <Navbar.Brand href="addReview">Movie Reviews</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link href="/">See Reviews</Nav.Link>
                        <Nav.Link href="addReview">Add Review</Nav.Link>
                        
                    </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <nav>
                <Link to="addReview">Add a Review</Link>
            </nav>
            <span role="img" aria-label="star">➡️</span>
            <Emoji symbol="😀" label="happy"/>
            <Emoji symbol="⭐" label="star"/>
            
            <h1>Star Wars Reviews</h1>
            { movies.map( (movie) => (
                <><h2>{movie.name}</h2>
                <img src={"./images/" + movie.poster} />
                <p>Release Date: {movie.date}</p>
                <p>Main Actors: {movie.actors}</p>
                <p>Rating: {emjoi(movie.rating)}</p>
                <button className="delete" onClick={() => handleRemove(movie.name)}>Delete</button>
                <br></br></>
            ))}           
        </div>
    );
}

